import allCountries from './country_names.json';

export default (req, res) => {
  const { country_name } = req.query;
  console.log(country_name); 
  let queriedCountries; 

  if (country_name) queriedCountries = allCountries.filter(country => country.name.toLowerCase().includes(country_name.toLowerCase()));
  else queriedCountries = allCountries[Math.floor(Math.random() * allCountries.length)];  

  console.log(queriedCountries);

  res.status(200).json({
      country: queriedCountries.name, 
      emoji: queriedCountries.emoji 
  });
};
