import allCountries from './country_names.json';

export default function getCountryData(name) {
    const country = allCountries[returnCountryIndex(name)];
    const country_name = country.name, emoji_name = country.emoji;  
    return {
        name: country_name, 
        emoji: emoji_name
    }
}

export function getRandomCountry() {
    const country = allCountries[Math.floor(Math.random() * allCountries.length)]; 
    const country_name = country.name, emoji_name = country.emoji;  
    return {
        name: country_name, 
        emoji: emoji_name
    }
}

function returnCountryIndex(name) {
    for (let i = 0; i < allCountries.length; i++) {
        if (allCountries[i].name === name) {
            return allCountries.indexOf(name); 
        }
    }
}
