import allCountries from './country_names.json';

export default (req, res) => {
  const { country } = req.query;
  let queriedCountries = allCountries; 


  if (country) queriedCountries = allCountries.filter(quote => quote.name.toLowerCase().includes(country));
  else queriedCountries = allCountries[Math.random() * allCountries.length]; 

  res.status(200).json(queriedCountries);
};
