import allCountries from './country_names.json';

export default (req, res) => {
  const { country_name } = req.query;
  let queriedCountries = allCountries; 

  if (request) queriedCountries = queriedCountries.filter(country => country.name.toLowerCase().includes(country_name.toLowerCase()));
  else queriedCountries = queriedCountries[Math.floor(Math.random() * quotes.length)];  

  res.status(200).json(JSON.stringify(queriedCountries));
};
