import allCountries from './country_names.json';

export default (req, res) => {
  const { country } = req.query;
  let queriedCountries = allCountries; 

  if (country) queriedCountries = queriedCountries.filter(quote => quote.name.toLowerCase().includes(country));
  else queriedCountries = queriedCountries[Math.random() * allCountries.length]; 

  console.log(queriedCountries); 
  res.status(200).json(queriedCountries);
};
